import React, { Component } from "react";
import Draggable from "react-draggable";
import PdfReader from "./PdfReader";
import "./Icon.css";

export default class extends Component {
  constructor(props) {
    super(props);

    this.state = {
      pdfReader: false
    };

    this.map = {
      pdf: {
        path: "/ressources/icons/pdf.png",
        actionOpen: this.openPdfReader.bind(this),
        actionClose: this.closePdfReader.bind(this),
        component: (
          <PdfReader url={this.props.url} onClose={this.closePdfReader()} />
        )
      },
      jpg: {
        path: "/ressources/icons/1600.png",
        actionClose: null,
        actionOpen: null
      }
    };

    // Bind "this"
    this.openPdfReader = this.openPdfReader.bind(this);
  }

  openPdfReader() {
    this.setState({ pdfReader: true });
  }

  closePdfReader() {
    this.setState({ pdfReader: false });
  }

  render() {
    const icon = this.map[this.props.type];
    if (icon == null) return <></>;
    return (
      <>
        <Draggable>
          <div id="all">
            <img src={icon.path} onClick={() => icon.actionOpen()} />
            <p>{this.props.name + "." + this.props.type}</p>
          </div>
        </Draggable>
        {this.state.pdfReader && icon.component}
      </>
    );
  }
}
