import React, { Component } from "react";
import Draggable from "react-draggable";
import PdfReader from "./PdfReader";
import "./Icon.css";
import ImgReader from "./ImgReader";

export default class extends Component {
  constructor(props) {
    super(props);

    this.state = {
      reader: false
    };

    this.map = {
      pdf: {
        path: "/ressources/icons/pdf.png",
        component: (
          <PdfReader url={this.props.url} onClose={() => this.closeReader()} />
        )
      },
      jpg: {
        path: "/ressources/icons/1600.png",
        component: (
          <ImgReader url={this.props.url} onClose={() => this.closeReader()} />
        )
      }
    };

    // Bind "this"
    this.openReader = this.openReader.bind(this);
    this.closeReader = this.closeReader.bind(this);
  }

  openReader() {
    this.setState({ reader: true });
  }

  closeReader() {
    this.setState({ reader: false });
  }

  render() {
    const icon = this.map[this.props.type];
    if (icon == null) return <></>;
    return (
      <>
        <Draggable>
          <div id="all">
            <img src={icon.path} onClick={() => this.openReader()} />
            <p>{this.props.name + "." + this.props.type}</p>
          </div>
        </Draggable>
        {this.state.reader && icon.component}
      </>
    );
  }
}
