import React, { Component } from "react";
import "./Icon.css";
import Draggable from "react-draggable";

export default class extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    const image =
      this.props.type == "pdf"
        ? "/ressources/icons/pdf.png"
        : "/ressources/icons/1600.png";
    return (
      <Draggable>
        <div id="all">
          <img src={image} />
          <p>{this.props.name + "." + this.props.type}</p>
        </div>
      </Draggable>
    );
  }
}
