import React, { Component } from "react";
import "./Icon.css";
import Draggable from "react-draggable";
import PdfReader from "./PdfReader";

export default class extends Component {
  constructor(props) {
    super(props);

    this.state = {
      pdfReader: false
    };

    // Bind "this"
    this.openPdfReader = this.openPdfReader.bind(this);
  }

  openPdfReader() {
    this.setState({ pdfReader: true });
  }

  render() {
    const image =
      this.props.type == "pdf"
        ? "/ressources/icons/pdf.png"
        : "/ressources/icons/1600.png";

    return (
      <>
        <Draggable>
          <div id="all">
            <img src={image} onClick={this.openPdfReader()} />
            <p>{this.props.name + "." + this.props.type}</p>
          </div>
        </Draggable>
        {this.state.pdfReader && <PdfReader url={this.props.url} />}
      </>
    );
  }
}
